let deferredPrompt;"serviceWorker"in navigator&&window.addEventListener("load",function(){navigator.serviceWorker.register("service_worker.js")}),window.addEventListener("beforeinstallprompt",e=>{e.preventDefault(),e.prompt(),deferredPrompt.userChoice.then(e=>{"accepted"===e.outcome?console.log("User accepted the A2HS prompt"):console.log("User dismissed the A2HS prompt"),deferredPrompt=null})}),window.addEventListener("appinstalled",e=>{app.logEvent("a2hs","installed")});